const { promisify } = require('util')
const dbRun = promisify(db.run).bind(db)
const dbGet = promisify(db.get).bind(db)
const dbAll = promisify(db.all).bind(db)

module.exports = {
  async adiciona (post) {
    try {
      await dbRun('INSERT INTO posts (titulo, conteudo, autor) VALUES (?, ?, ?)', [
        post.titulo,
        post.conteudo,
        post.autor
      ])
    } catch (erro) {
      throw new InternalServerError('Erro ao adicionar o post!')
    }
  },

  async listarPorAutor (idAutor) {
    try {
      return await dbAll('SELECT id, titulo FROM posts WHERE autor = ?', [idAutor])
    } catch (erro) {
      throw new InternalServerError('Erro ao listar os posts!')
    }
  },

  async listarTodos () {
    try {
      return await dbAll('SELECT id, titulo, conteudo, autor FROM posts')
    } catch (erro) {
      throw new InternalServerError('Erro ao listar os posts!')
    }
  },

  async buscaPorId (id, idAutor) {
    try {
      let instrucoes = 'SELECT * FROM posts WHERE id = ?'
      const parametros = [id]

      idAutor = Number(idAutor)
      if (isNaN(idAutor) === false) {
        instrucoes = `${instrucoes} AND autor = ?`
        parametros.push(idAutor)
      }

      return await dbGet(instrucoes, parametros)
    } catch (erro) {
      throw new InternalServerError('Não foi possível encontrar o post!')
    }
  },

  async remover ({ id, autor }) {
    try {
      return await dbRun('DELETE FROM posts WHERE id = ? AND autor = ?', [id, autor])
    } catch (erro) {
      throw new InternalServerError('Erro ao tentar remover o post!')
    }
  }
}

const postsDao = require('./posts-dao')
const validacoes = require('../validacoes-comuns')

class Post {
  constructor (post) {
    this.id = post.id
    this.titulo = post.titulo
    this.conteudo = post.conteudo
    this.autor = post.autor
    this.valida()
  }

  adiciona () {
    return postsDao.adiciona(this)
  }

  static async buscaPorId (id) {
    const post = await postsDao.buscaPorId(id)
    if (!post) {
      return null
    }

    return new Post(post)
  }

  static async buscaPorIdAutor (id, idAutor) {
    const post = await postsDao.buscaPorId(id, idAutor)

    if (!post) {
      return null
    }

    return new Post(post)
  }

  valida () {
    validacoes.campoStringNaoNulo(this.titulo, 'titulo')
    validacoes.campoTamanhoMinimo(this.titulo, 'titulo', 5)

    validacoes.campoStringNaoNulo(this.conteudo, 'conteudo')
    validacoes.campoTamanhoMaximo(this.conteudo, 'conteudo', 140)
  }

  remover () {
    return postsDao.remover(this)
  }

  static listarPorAutor (idAutor) {
    return postsDao.listarPorAutor(idAutor)
  }

  static listarTodos () {
    return postsDao.listarTodos()
  }
}

module.exports = Post

