    try {
      const id = await tokens.access.verifica(token)
      const usuario = await Usuario.buscaPorId(id)
      done(null, usuario, { token })
    } catch (erro) {
      done(erro)
    }
  })
)

const passport = require('passport')
const Usuario = require('./usuarios-modelo')
const tokens = require('./tokens')

module.exports = {
  local (req, res, next) {
    passport.authenticate(
      'local',
      { session: false },
      (erro, usuario, info) => {
        if (erro) {
          return next(erro)
        }

        req.user = usuario
        req.estaAutenticado = true
        return next()
      }
    )(req, res, next)
  },

  bearer (req, res, next) {
    passport.authenticate(
      'bearer',
      { session: false },
      (erro, usuario, info) => {
        if (erro) {
          return next(erro)
        }

        req.token = info.token
        req.user = usuario
        req.estaAutenticado = true
        return next()
      }
    )(req, res, next)
  },

  async refresh (req, res, next) {
    try {
      const { refreshToken } = req.body
      const id = await tokens.refresh.verifica(refreshToken)
      await tokens.refresh.invalida(refreshToken)
      req.user = await Usuario.buscaPorId(id)
      return next()
    } catch (erro) {
      if (erro.name === 'InvalidArgumentError') {
        return res.status(401).json({ erro: erro.message })
      }
      return res.status(500).json({ erro: erro.message })
    }
  },

  async verificacaoEmail (req, res, next) {
    try {
      const { token } = req.params
      const id = await tokens.verificacaoEmail.verifica(token)
      const usuario = await Usuario.buscaPorId(id)
      req.user = usuario
      next()
    } catch (erro) {
      next(erro)
    }
  }
}

const jwt = require('jsonwebtoken')
const crypto = require('crypto')
const moment = require('moment')

const { InvalidArgumentError } = require('../erros')

const allowlistRefreshToken = require('../../redis/allowlist-refresh-token')
const blocklistAccessToken = require('../../redis/blocklist-access-token')

function criaTokenJWT (id, [tempoQuantidade, tempoUnidade]) {
  const payload = { id }
  const token = jwt.sign(payload, process.env.CHAVE_JWT, {
    expiresIn: tempoQuantidade + tempoUnidade
  })
  return token
}

async function verificaTokenJWT (token, nome, blocklist) {
  await verificaTokenNaBlocklist(token, nome, blocklist)
  const { id } = jwt.verify(token, process.env.CHAVE_JWT)
  return id
}

async function verificaTokenNaBlocklist (token, nome, blocklist) {
  if (!blocklist) {
    return
  }

  const tokenNaBlocklist = await blocklist.contemToken(token)
  if (tokenNaBlocklist) {
    throw new jwt.JsonWebTokenError(`${nome} inválido por logout!`)
  }
}

function invalidaTokenJWT (token, blocklist) {
  return blocklist.adiciona(token)
}

async function criaTokenOpaco (id, [tempoQuantidade, tempoUnidade], allowlist) {
  const tokenOpaco = crypto.randomBytes(24).toString('hex')
  const dataExpiracao = moment().add(tempoQuantidade, tempoUnidade).unix()
  await allowlist.adiciona(tokenOpaco, id, dataExpiracao)
  return tokenOpaco
}

async function verificaTokenOpaco (token, nome, allowlist) {
  verificaTokenEnviado(token, nome)
  const id = await allowlist.buscaValor(token)
  verificaTokenValido(id, nome)
  return id
}

async function invalidaTokenOpaco (token, allowlist) {
  await allowlist.deleta(token)
}

function verificaTokenValido (id, nome) {
  if (!id) {
    throw new InvalidArgumentError(`${nome} inválido!`)
  }
}

function verificaTokenEnviado (token, nome) {
  if (!token) {
    throw new InvalidArgumentError(`${nome} não enviado!`)
  }
}

module.exports = {
  access: {
    nome: 'access token',
    lista: blocklistAccessToken,
    expiracao: [15, 'm'],
    cria (id) {
      return criaTokenJWT(id, this.expiracao)
    },
    verifica (token) {
      return verificaTokenJWT(token, this.nome, this.lista)
    },
    invalida (token) {
      return invalidaTokenJWT(token, this.lista)
    }
  },
  refresh: {
    nome: 'refresh token',
    lista: allowlistRefreshToken,
    expiracao: [5, 'd'],
    cria (id) {
      return criaTokenOpaco(id, this.expiracao, this.lista)
    },
    verifica (token) {
      return verificaTokenOpaco(token, this.nome, this.lista)
    },
    invalida (token) {
      return invalidaTokenOpaco(token, this.lista)
    }
  },
  verificacaoEmail: {
    nome: 'token de verificação de e-mail',
    expiracao: [1, 'h'],
    cria (id) {
      return criaTokenJWT(id, this.expiracao)
    },
    verifica (token) {
      return verificaTokenJWT(token, this.nome)
    }
  }
}

const Usuario = require('./usuarios-modelo')
const { InvalidArgumentError } = require('../erros')

const tokens = require('./tokens')
const { EmailVerificacao } = require('./emails')
const { ConversorUsuario } = require('../conversores')

function geraEndereco (rota, token) {
  const baseURL = process.env.BASE_URL
  return `${baseURL}${rota}${token}`
}

module.exports = {
  async adiciona (req, res, proximo) {
    const { nome, email, senha, cargo } = req.body

    try {
      const usuario = new Usuario({
        nome,
        email,
        emailVerificado: false,
        cargo
      })
      await usuario.adicionaSenha(senha)
      await usuario.adiciona()

      const token = tokens.verificacaoEmail.cria(usuario.id)
      const endereco = geraEndereco('/usuario/verifica_email/', token)
      const emailVerificacao = new EmailVerificacao(usuario, endereco)
      emailVerificacao.enviaEmail().catch(console.log)

      res.status(201).json()
    } catch (erro) {
      proximo(erro)
    }
  },

  async login (req, res, proximo) {
    try {
      const accessToken = tokens.access.cria(req.user.id)
      const refreshToken = await tokens.refresh.cria(req.user.id)
      res.set('Authorization', accessToken)
      res.status(200).json({ refreshToken })
    } catch (erro) {
      proximo(erro)
    }
  },

  async logout (req, res, proximo) {
    try {
      const token = req.token
      await tokens.access.invalida(token)
      res.status(204).json()
    } catch (erro) {
      proximo(erro)
    }
  },

  async lista (req, res, proximo) {
    try {
      const usuarios = await Usuario.lista()
      const conversor = new ConversorUsuario(
        'json',
        req.acesso.todos.permitido ? req.acesso.todos.atributos : req.acesso.apenasSeu.atributos
      )
      res.send(conversor.converter(usuarios))
    } catch (erro) {
      proximo(erro)
    }
  },

  async verificaEmail (req, res, proximo) {
    try {
      const usuario = req.user
      await usuario.verificaEmail()
      res.status(200).json()
    } catch (erro) {
      proximo(erro)
    }
  },

  async deleta (req, res, proximo) {
    try {
      const usuario = await Usuario.buscaPorId(req.params.id)
      await usuario.deleta()
      res.status(200).json()
    } catch (erro) {
      proximo(erro)
    }
  }
}

